@Composable
fun BookDetailsScreen(
 book: Book,
 modifier: Modifier = Modifier,
 viewModel: BookDetailsViewModel = viewModel()
) {
 // Initialize with the book
 LaunchedEffect(book.id) {
 viewModel.getBookDetails(book)
