 val context = LocalContext.current
 val inputMethodManager = remember { context.getSystemService(Context.INPUT_METHOD_SERVICE) as InputMethodManager }
 val focusManager = LocalFocusManager.current

 OutlinedTextField(
 value = searchQuery,
 onValueChange = onQueryChange,
 leadingIcon = {
 Icon(
 imageVector = Icons.Default.Search,
 contentDescription = stringResource(R.string.search)
 )
 },
 label = { Text(stringResource(R.string.search)) },
 singleLine = true,
 modifier = modifier
 .fillMaxWidth()
 .padding(16.dp)
 .focusRequester(focusRequester)
 .onFocusChanged { state ->
 if (!state.isFocused && searchQuery.isNotEmpty()) {
 // Hide keyboard when focus is lost
 inputMethodManager.hideSoftInputFromWindow(focusRequester.findFocus()?.windowToken, 0)
 }
 },
 keyboardOptions = KeyboardOptions(
 imeAction = ImeAction.Search
 ),
 keyboardActions = KeyboardActions(
 onSearch = { 
 onSearch()
 focusManager.clearFocus()
 val view = focusRequester.findFocus() 
 if (view != null) {
 inputMethodManager.hideSoftInputFromWindow(view.windowToken, 0)
 }
 }
 )
 )
}
